export default function createBidirectionalToolData(bidirectionalData, viewport) {
  const {
    majorAxis,
    minorAxis,
    label = '',
    FrameOfReferenceUID,
    referencedImageId
  } = bidirectionalData;
  const [major0, major1] = majorAxis;
  const [minor0, minor1] = minorAxis;
  const {
    viewUp,
    viewPlaneNormal
  } = viewport.getCamera();
  const points = [major0, major1, minor0, minor1];
  const bidirectionalToolData = {
    highlighted: true,
    invalidated: true,
    metadata: {
      toolName: 'Bidirectional',
      viewPlaneNormal,
      viewUp,
      FrameOfReferenceUID,
      referencedImageId
    },
    data: {
      handles: {
        points,
        textBox: {
          hasMoved: false,
          worldPosition: [0, 0, 0],
          worldBoundingBox: {
            topLeft: [0, 0, 0],
            topRight: [0, 0, 0],
            bottomLeft: [0, 0, 0],
            bottomRight: [0, 0, 0]
          }
        },
        activeHandleIndex: null
      },
      label,
      cachedStats: {}
    },
    isLocked: false,
    isVisible: true
  };
  return bidirectionalToolData;
}
